package basic;

public class sample1 {

	public static void main(String[] args) {
		int x=2;
		int r=x%2;
		switch(r)
		{
		case 0:
			System.out.println("the number is even");
			break;
		case 1:
			System.out.println("the number is odd");
			break;
		default:
			System.out.println("invalid");
			break;
		}
		// TODO Auto-generated method stub

	}

}
